/* Function: mdlOutputs =======================================================
 *
*/
static void mdlOutputs(SimStruct *S, int_T tid)
{
    //get sockFd and controllerScheme
    int_T *internalVars = ssGetIWork(S); /* integer typed*/    
    int controlScheme = internalVars[CONTROLSCHEME];
    int sockfd        = internalVars[SOCKFD];
   
    if(sockfd == 0) //failed to initialize
        return;
    
    const real_T   *torque          = (const real_T*) ssGetInputPortSignal(S,0);
    
    if(controlScheme == -1) { //first run
        const real_T   *controllerType  = (const real_T*) ssGetInputPortSignal(S,1);
        internalVars[CONTROLSCHEME]     = (int)controllerType[0]; //update work vector
        controlScheme                   = internalVars[CONTROLSCHEME]; //reset this value
    }

    int numbytes, MAXDATASIZE;
    if(controlScheme == 0) //joint level controller    
        MAXDATASIZE = 70;
    else if(controlScheme == 1) //cartesian controller
        MAXDATASIZE = 55;
    
    char buf[MAXDATASIZE];
    
    int size = -1;
    
    if(controlScheme == 0) { //joint level controller    
        size = sprintf(buf, "%08.4f, %08.4f, %08.4f, %08.4f, %08.4f, %08.4f, %08.4f", torque[0], torque[1], torque[2], torque[3], torque[4], torque[5], torque[6]);    
    }
    else if(controlScheme == 1) { //cartesian controller    
        size = sprintf(buf, "%08.4f,%08.4f,%08.4f,%08.4f,%08.4f,%08.4f", torque[0], torque[1], torque[2], torque[3], torque[4], torque[5]);
    }
      
    if ((numbytes = send(sockfd, buf, MAXDATASIZE-1, 0)) == -1) {
        //mexPrintf("Send Failed\n");
    }        
}



/* Function: mdlTerminate =====================================================
 * Abstract:
 *    In this function, you should perform any actions that are necessary
 *    at the termination of a simulation.  For example, if memory was
 *    allocated in mdlStart, this is the place to free it.
 */
static void mdlTerminate(SimStruct *S)
{
    //get sockFd and controllerScheme
    int_T *internalVars = ssGetIWork(S); /* integer typed*/    
    int controlScheme = internalVars[CONTROLSCHEME];
    int sockfd        = internalVars[SOCKFD];
    
    int numbytes;
    int MAXDATASIZE = 30;
    char buf[MAXDATASIZE];
    
    int size = -1;
    if(controlScheme == 0) //joint level controller  
        size = sprintf(buf, "%i, %i, %i, %i, %i, %i, %i", -1,-1,-1,-1,-1,-1,-1);
    else if(controlScheme == 1) //cartesian controller 
        size = sprintf(buf, "%i, %i, %i, %i, %i, %i", -1,-1,-1,-1,-1,-1);        
    
    if ((numbytes = send(sockfd, buf, MAXDATASIZE-1, 0)) == -1) {
        mexPrintf("Send Failed\n");
    }
    close(sockfd);
    mexPrintf("Closing torque socket (sockfd = %i)\n", sockfd);
}
